mapper.delete:
DELETE FROM tb_config_item_mapping t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_config_item_mapping t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.config_item_code = #{configItemCode}


    AND t.source_system = #{sourceSystem}


    AND t.source_code = #{sourceCode}


    AND t.source_value = #{sourceValue}


    AND t.target_option_code = #{targetOptionCode}


    AND t.target_value = #{targetValue}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC

SELECT 
FROM tb_config_item_mapping t
WHERE t.row_valid = 1


    AND t.config_item_code = #{configItemCode}


    AND t.source_system = #{sourceSystem}

ORDER BY t.id DESC

SELECT 
FROM tb_config_item_mapping t
WHERE t.row_valid = 1
AND t.config_item_code = #{configItemCode}
AND t.source_system = #{sourceSystem}
AND t.source_code = #{sourceCode}
AND t.source_value IS NULL
LIMIT 1

SELECT 
FROM tb_config_item_mapping t
WHERE t.row_valid = 1
AND t.config_item_code = #{configItemCode}
AND t.source_system = #{sourceSystem}
AND t.source_code = #{sourceCode}
AND t.source_value = #{sourceValue}
LIMIT 1
