mapper.delete:
DELETE FROM tb_device t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_device t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.code = #{code}


    AND t.name = #{name}


    AND t.name_en = #{nameEn}


    AND t.type = #{type}


    AND t.device_item = #{ecuType}


    AND t.func_domain = #{funcDomain}


    AND t.node_type = #{nodeType}


    AND t.ota_support = #{otaSupport}


    AND t.link_config_source = #{linkConfigSource}


    AND t.link_flash_target = #{linkFlashTarget}


    AND t.comm_protocol = #{commProtocol}


    AND t.flash_protocol = #{flashProtocol}


    AND t.can_tx_id = #{canTxId}


    AND t.can_rx_id = #{canRxId}


    AND t.ethernet_ip = #{ethernetIp}


    AND t.doip_gateway_id = #{doipGatewayId}


    AND t.doip_entity_id = #{doipEntityId}


    AND t.core = #{core}


    AND t.sort = #{sort}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC

SELECT 
FROM tb_device t
WHERE t.row_valid = 1


    AND t.code = #{code}


    AND t.name LIKE #{name}


    AND t.func_domain = #{funcDomain}

ORDER BY t.id DESC
