mapper.delete:
DELETE FROM tb_supplier t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_supplier t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.code = #{code}


    AND t.name = #{name}


    AND t.name_short = #{nameShort}


    AND t.name_en = #{nameEn}


    AND t.type = #{type}


    AND t.province = #{province}


    AND t.city = #{city}


    AND t.county = #{county}


    AND t.subdistrict = #{subdistrict}


    AND t.address = #{address}


    AND t.zipcode = #{zipcode}


    AND t.fax = #{fax}


    AND t.tel = #{tel}


    AND t.website = #{website}


    AND t.email = #{email}


    AND t.contact_person = #{contactPerson}


    AND t.contact_person_tel = #{contactPersonTel}


    AND t.legal_person = #{legalPerson}


    AND t.bank_name = #{bankName}


    AND t.account_no = #{accountNo}


    AND t.tax_no = #{taxNo}


    AND t.enable = #{enable}


    AND t.sort = #{sort}


    AND t.description = #{description}


    AND t.create_by = #{createBy}


    AND t.create_time = #{createTime}


    AND t.modify_by = #{modifyBy}


    AND t.modify_time = #{modifyTime}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC

SELECT 
FROM tb_supplier t
WHERE t.row_valid = 1


    AND t.code = #{code}


    AND t.name LIKE #{name}

ORDER BY t.id DESC
