mapper.delete:
DELETE FROM tb_veh_build_config t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_veh_build_config t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.platform_code = #{platformCode}


    AND t.series_code = #{seriesCode}


    AND t.model_code = #{modelCode}


    AND t.base_model_code = #{baseModelCode}


    AND t.code = #{code}


    AND t.name = #{name}


    AND t.name_en = #{nameEn}


    AND t.vehicle_stage_code = #{vehicleStageCode}


    AND t.exterior_code = #{exteriorCode}


    AND t.interior_code = #{interiorCode}


    AND t.wheel_code = #{wheelCode}


    AND t.tire_code = #{tireCode}


    AND t.spare_tire_code = #{spareTireCode}


    AND t.adas_code = #{adasCode}


    AND t.seat_code = #{seatCode}


    AND t.enable = #{enable}


    AND t.sort = #{sort}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC

SELECT 
FROM tb_veh_build_config t
WHERE t.row_valid = 1


    AND t.platform_code = #{platformCode}


    AND t.series_code = #{seriesCode}


    AND t.model_code = #{modelCode}


    AND t.base_model_code = #{baseModelCode}


    AND t.code = #{code}


    AND (t.name LIKE #{name} OR t.name_en LIKE #{name})


    and date_format(t.create_time,'%Y%m%d') >= date_format(#{beginTime},'%Y%m%d')


    and date_format(t.create_time,'%Y%m%d') <= date_format(#{endTime},'%Y%m%d')

ORDER BY t.sort ASC, t.id DESC

SELECT COUNT(*)
FROM tb_veh_build_config t
WHERE t.row_valid = 1


    AND t.platform_code = #{platformCode}


    AND t.series_code = #{seriesCode}


    AND t.model_code = #{modelCode}


    AND t.base_model_code = #{baseModelCode}


    AND t.vehicle_stage_code = #{vehicleStageCode}


    AND t.exterior_code = #{exteriorCode}


    AND t.interior_code = #{interiorCode}


    AND t.wheel_code = #{wheelCode}


    AND t.tire_code = #{tireCode}
