mapper.select:
SELECT 
FROM tb_veh_part t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.sn = #{sn}


    AND t.no = #{no}


    AND t.ecu = #{ecu}


    AND t.ecu_config_word = #{ecuConfigWord}


    AND t.supplier_code = #{supplierCode}


    AND t.hardware_ver = #{hardwareVer}


    AND t.software_ver = #{softwareVer}


    AND t.hardware_no = #{hardwareNo}


    AND t.software_no = #{softwareNo}


    AND t.mac = #{mac}


    AND t.hsm = #{hsm}


    AND t.extra = #{extra}


    AND t.vin = #{vin}


    AND t.sort = #{sort}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC

SELECT 
FROM tb_veh_part t
WHERE t.row_valid = 1
AND ecu = #{ecu}
AND sn = #{sn}
LIMIT 1
