mapper.delete:
DELETE FROM tb_vehicle_config_item t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_vehicle_config_item t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.vin = #{vin}


    AND t.version = #{version}


    AND t.config_item_code = #{configItemCode}


    AND t.config_item_value = #{configItemValue}


    AND t.source_value = #{sourceValue}


    AND t.source_system = #{sourceSystem}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC

SELECT FROM tb_vehicle_config_item t WHERE t.row_valid = 1
AND t.vin = #{vin}
ORDER BY t.id DESC
