mapper.delete:
DELETE FROM tb_vehicle_part_history t
WHERE t.id IN

    #{id}

mapper.select:
SELECT 
FROM tb_vehicle_part_history t
WHERE t.row_valid = 1

    AND t.id = #{id}


    AND t.pn = #{pn}


    AND t.vin = #{vin}


    AND t.device_code = #{deviceCode}


    AND t.device_item = #{deviceItem}


    AND t.sn = #{sn}


    AND t.config_word = #{configWord}


    AND t.supplier_code = #{supplierCode}


    AND t.batch_num = #{batchNum}


    AND t.hardware_ver = #{hardwareVer}


    AND t.software_ver = #{softwareVer}


    AND t.hardware_pn = #{hardwarePn}


    AND t.software_pn = #{softwarePn}


    AND t.extra = #{extra}


    AND t.bind_time = #{bindTime}


    AND t.bind_type = #{bindType}


    AND t.bind_by = #{bindBy}


    AND t.bind_org = #{bindOrg}


    AND t.unbind_time = #{unbindTime}


    AND t.unbind_reason = #{unbindReason}


    AND t.unbind_by = #{unbindBy}


    AND t.unbind_org = #{unbindOrg}


    AND t.part_state = #{partState}


    AND t.description = #{description}


    AND t.create_time = #{createTime}


    AND t.create_by = #{createBy}


    AND t.modify_time = #{modifyTime}


    AND t.modify_by = #{modifyBy}


    AND t.row_version = #{rowVersion}

ORDER BY t.id DESC
